import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

function Login() {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const navigate = useNavigate();

    const handleSubmit = async (event) => {
        event.preventDefault();
        try {
            const baseURL = process.env.REACT_APP_API_URL || 'http://localhost:5000';
            const response = await axios.post(`${baseURL}/login`, {
                username,
                password
            });

            // Check if the response has the expected data
            if (response.data && response.data.user && response.data.token) {
                const { token, user } = response.data;

                // Store both the token and the user data in localStorage
                localStorage.setItem('token', token);
                localStorage.setItem('userId', user.id);
                localStorage.setItem('username', user.username);
                localStorage.setItem('painIntensity', user.painIntensity);
                localStorage.setItem('painLocation', user.painLocation);

                alert('Login Successful');
                navigate('/dashboard'); // Redirect to the dashboard page using navigate
            } else {
                alert('Login Failed: Incorrect server response format');
                console.error('Login Failed: Incorrect server response format', response.data);
            }
        } catch (error) {
            if (error.response) {
                console.error("Response error:", error.response.data);
                alert('Login Failed: ' + error.response.data.message);
            } else if (error.request) {
                console.error("Request error:", error.request);
                alert('Login Failed: No response from the server');
            } else {
                console.error("Error:", error.message);
                alert('Login Failed: ' + error.message);
            }
        }
    };

    return (
        <div>
            <h1>Log in</h1>
            <form onSubmit={handleSubmit}>
                <input
                    type="text"
                    value={username}
                    onChange={(e) => setUsername(e.target.value)}
                    placeholder="Username"
                    required
                />
                <input
                    type="password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    placeholder="Password"
                    required
                />
                <button type="submit">Login</button>
            </form>
        </div>
    );
}

export default Login;
