import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom'; // Import useNavigate from React Router

function Login() {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const navigate = useNavigate(); // Create a navigate function

    const handleSubmit = async (event) => {
        event.preventDefault();
        try {
            // Ensure the environment variable for the API URL is set correctly
            const baseURL = process.env.REACT_APP_API_URL || 'http://localhost:5000';
            const response = await axios.post(`${baseURL}/login`, {
                username,
                password
            });
            const { token, user } = response.data;

            // Store both the token and the user data in localStorage
            localStorage.setItem('token', token);
            localStorage.setItem('userId', user.id);
            localStorage.setItem('username', user.username);
            localStorage.setItem('painIntensity', user.painIntensity);
            localStorage.setItem('painLocation', user.painLocation);

            alert('Login Successful');
            navigate('/dashboard'); // Redirect to the dashboard page using navigate
        } catch (error) {
            if (error.response) {
                // The request was made and the server responded with a status code
                // that falls out of the range of 2xx
                console.error("Response error:", error.response.data);
                alert('Login Failed: ' + error.response.data.message); // Show more specific error from server
            } else if (error.request) {
                // The request was made but no response was received
                console.error("Request error:", error.request);
                alert('Login Failed: No response from server');
            } else {
                // Something happened in setting up the request that triggered an Error
                console.error("Error:", error.message);
                alert('Login Failed: ' + error.message);
            }
        }
    };

    return (
        <div>
            <h1>Log in</h1>
            <form onSubmit={handleSubmit}>
                <input
                    type="text"
                    value={username}
                    onChange={(e) => setUsername(e.target.value)}
                    placeholder="Username"
                    required
                />
                <input
                    type="password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    placeholder="Password"
                    required
                />
                <button type="submit">Login</button>
            </form>
        </div>
    );
}

export default Login;
