import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom'; // Import useNavigate for programmatic navigation

const Dashboard = () => {
    const [userData, setUserData] = useState({ painIntensity: '', painLocation: '' });
    const [selectedExercises, setSelectedExercises] = useState([]);
    const [finishedTherapies, setFinishedTherapies] = useState(0);
    const [therapyHistory, setTherapyHistory] = useState([]);
    const [error, setError] = useState('');
    const [loading, setLoading] = useState(true);
    const navigate = useNavigate(); // For navigation

    useEffect(() => {
        const baseURL = process.env.REACT_APP_API_URL || 'http://localhost:5000';
        const username = localStorage.getItem('username');
        const token = localStorage.getItem('token');

        const fetchUserData = async () => {
            try {
                const response = await axios.get(`${baseURL}/user/${username}`, {
                    headers: {
                        Authorization: `Bearer ${token}`
                    }
                });
                setUserData(response.data);
                const randomExercises = getRandomExercises(response.data.painLocation);
                setSelectedExercises(randomExercises);
                setLoading(false);
            } catch (error) {
                console.error('Error fetching user data:', error);
                setError('Failed to fetch user data. Please try again.');
                setLoading(false);
            }
        };

        fetchUserData();
    }, []);

    const handleFinishTherapy = () => {
        setFinishedTherapies(finishedTherapies + 1);
        const today = new Date().toLocaleDateString();
        const todayExercises = selectedExercises.map(ex => ({
            date: today,
            name: ex.name,
            description: ex.description
        }));
        setTherapyHistory([...therapyHistory, ...todayExercises]);
    };

    const handleViewHistory = () => {
        navigate('/history', { state: { history: therapyHistory } }); // Pass history as state to the route
    };

    if (loading) {
        return <p>Loading...</p>;
    }

    return (
        <div>
            <h1>Today's Therapy</h1>
            <p>Date: {new Date().toLocaleDateString()}</p>
            {error ? (
                <p>{error}</p>
            ) : (
                <>
                    <p>Pain Intensity: {userData.painIntensity}</p>
                    <p>Pain Location: {userData.painLocation}</p>
                    <h2>Recommended Exercises</h2>
                    <ul>
                        {selectedExercises.map((exercise, index) => (
                            <li key={index}>
                                <strong>{exercise.name}</strong>: {exercise.description}
                            </li>
                        ))}
                    </ul>
                    <button onClick={handleFinishTherapy}>Done</button>
                    <button onClick={handleViewHistory}>View History</button>
                </>
            )}
        </div>
    );
};

export default Dashboard;
